// Input: z Get many collections dostaneš items jako
// [{ json: { name: "digitalGear" } }, { json: { name: "gearCategories" } }, ...]
// Cíl: pro každý název kolekce vytvořit jeden item, aby se přes něj
// mohl spustit Firestore node "Get Many Documents" s dynamickým názvem kolekce.

const results = [];

// Projdi všechny vstupní položky (každá má json.name = název kolekce)
for (const item of $input.all()) {
  const collectionName = item.json.name;

  // Můžeš případně některé kolekce přeskočit, např. jen vybrané:
  // if (!['digitalGear', 'gearCategories', 'photos', 'posts', 'projects', 'yearlyStats'].includes(collectionName)) {
  //   continue;
  // }

  results.push({
    json: {
      collection: collectionName,
    },
  });
}

// Výstup: pole items, kde každý item má json.collection = název kolekce
return results;

// Cíl: poskládat výstup ve tvaru:
// {
//   digitalGear: [ {...doc1}, {...doc2}, ... ],
//   gearCategories: [ ... ],
//   photos: [ ... ],
//   posts: [ ... ],
//   projects: [ ... ],
//   yearlyStats: [ ... ]
// }

const backup = {};

const items = $input.all();

for (const item of items) {
  const data = item.json;

  const fullName = data._name;
  if (!fullName) {
    continue;
  }

  const parts = fullName.split('/');
  const docId = parts[parts.length - 1];
  const collectionName = parts[parts.length - 2];

  if (!collectionName) {
    continue;
  }

  if (!backup[collectionName]) {
    backup[collectionName] = [];
  }

  // Odfiltrujeme interní metadata, necháme si jen vlastní data + id
  const { _name, _createTime, _updateTime, ...cleanData } = data;

  backup[collectionName].push({
    id: docId,
    ...cleanData,
  });
}

// DŮLEŽITÉ: vždy vrátit pole objektů
return [
  {
    json: backup,
  },
];

